// Deduplicate & Normalize from Clay
const items = $input.all();
const seen = new Set();
const unique = [];

items.forEach(item => {
  const email = item.json.email?.toLowerCase();
  const key = email || `${item.json.first_name}_${item.json.last_name}_${item.json.company_name}`;

  if (!seen.has(key) && email) {
    seen.add(key);
    unique.push({
      json: {
        email: email,
        first_name: item.json.first_name?.trim(),
        last_name: item.json.last_name?.trim(),
        title: item.json.title?.trim(),
        organization_name: item.json.company_name?.trim(),
        organization_website_url: item.json.company_website?.trim(),
        organization_location: item.json.company_location?.trim() || 'Switzerland',
        industry: item.json.industry?.trim(),
        num_of_employees: item.json.company_size,
        phone: item.json.phone?.replace(/[^\d+]/g, ''),
        linkedin_url: item.json.linkedin_url?.trim(),
        organization_linkedin_url: item.json.company_linkedin_url?.trim(),

        // Clay enrichment data
        intent_signals: item.json.intent_signals || '',
        personalization_hook: item.json.personalization_hook || '',
        lead_score: parseInt(item.json.lead_score) || 0,
        lead_tier: item.json.lead_tier || 'Cold',

        // Metadata
        dedupe_key: key,
        discovered_at: new Date().toISOString(),
        source: 'Clay + LinkedIn Sales Navigator'
      }
    });
  }
});

return unique;

// Spam Gate: Filter freemail, role-based, blacklist
const items = $input.all();
const passed = [];

const freemailDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com', 'aol.com', 'icloud.com', 'protonmail.com', 'gmx.de', 'gmx.ch', 'web.de', 't-online.de', 'bluewin.ch'];
const roleBasedPrefixes = ['info@', 'contact@', 'sales@', 'support@', 'admin@', 'hello@', 'team@', 'office@', 'noreply@', 'kontakt@'];

items.forEach(item => {
  const email = item.json.email?.toLowerCase() || '';
  const domain = email.split('@')[1];

  const isFreemail = freemailDomains.includes(domain);
  const isRoleBased = roleBasedPrefixes.some(prefix => email.startsWith(prefix));

  if (!isFreemail && !isRoleBased && email) {
    passed.push({
      json: {
        ...item.json,
        spam_check_passed: true,
        spam_check_reason: 'OK'
      }
    });
  } else {
    // Log rejected
    console.log(`Rejected: ${email} - Freemail: ${isFreemail}, RoleBased: ${isRoleBased}`);
  }
});

return passed;

