// Calculate KPIs for last 7 days
const items = $input.all();
const sevenDaysAgo = new Date();
sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

// Filter last 7 days
const lastWeek = items.filter(item => {
  const enrichedAt = new Date(item.json.enriched_at || item.json.discovered_at);
  return enrichedAt >= sevenDaysAgo;
});

// Calculate metrics
const totalLeads = lastWeek.length;
const qualifiedLeads = lastWeek.filter(l => parseInt(l.json.lead_score) >= 60).length;
const hotLeads = lastWeek.filter(l => l.json.lead_tier === 'Hot').length;
const warmLeads = lastWeek.filter(l => l.json.lead_tier === 'Warm').length;

const emailsSent = lastWeek.filter(l => l.json.status === 'Sent' || l.json.sent_at).length;
const repliesReceived = lastWeek.filter(l => l.json.reply_at).length;
const positiveReplies = lastWeek.filter(l => l.json.reply_sentiment === 'POSITIVE').length;
const meetingsBooked = lastWeek.filter(l => l.json.status === 'Meeting Booked').length;

// Calculate rates
const qualificationRate = totalLeads > 0 ? ((qualifiedLeads / totalLeads) * 100).toFixed(1) : 0;
const replyRate = emailsSent > 0 ? ((repliesReceived / emailsSent) * 100).toFixed(1) : 0;
const positiveReplyRate = repliesReceived > 0 ? ((positiveReplies / repliesReceived) * 100).toFixed(1) : 0;
const meetingRate = emailsSent > 0 ? ((meetingsBooked / emailsSent) * 100).toFixed(1) : 0;

// Top performing leads
const topLeads = lastWeek
  .filter(l => l.json.lead_score)
  .sort((a, b) => parseInt(b.json.lead_score) - parseInt(a.json.lead_score))
  .slice(0, 5)
  .map(l => `${l.json.first_name} ${l.json.last_name} (${l.json.organization_name}) - Score: ${l.json.lead_score}`);

// Industry breakdown
const industries = {};
lastWeek.forEach(l => {
  const ind = l.json.industry || 'Unknown';
  industries[ind] = (industries[ind] || 0) + 1;
});

const topIndustries = Object.entries(industries)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 3)
  .map(([ind, count]) => `${ind}: ${count}`);

return [{
  json: {
    period: 'Last 7 Days',
    total_leads: totalLeads,
    qualified_leads: qualifiedLeads,
    hot_leads: hotLeads,
    warm_leads: warmLeads,
    emails_sent: emailsSent,
    replies_received: repliesReceived,
    positive_replies: positiveReplies,
    meetings_booked: meetingsBooked,
    qualification_rate: qualificationRate,
    reply_rate: replyRate,
    positive_reply_rate: positiveReplyRate,
    meeting_rate: meetingRate,
    top_leads: topLeads,
    top_industries: topIndustries
  }
}];

